/* IE placeholders fix */
window.addEvent('domready',function(){document.getElements('*[placeholder]').each(function(el){if(el.get('value')===''){el.set('value',el.getProperty('placeholder'))}
el.addEvents({'focus':function(){if(el.get('value')===el.getProperty('placeholder')){el.set('value','')}},'blur':function(){if(el.get('value')===''){el.set('value',el.getProperty('placeholder'))}}})})})
/* IE 10 viewport fix */
(function(){'use strict';if(navigator.userAgent.match(/IEMobile\/10\.0/)){var msViewportStyle=document.createElement('style')
msViewportStyle.appendChild(document.createTextNode('@-ms-viewport{width:auto!important}'))
document.querySelector('head').appendChild(msViewportStyle)}})()